entry.value:

var flowFile = session.get();
if(!flowFile != null) {
	try {

		var text  = "";

		//获取pot_outline属性
		var pot_outline = flowFile.getAttribute('outline')
		pot_outline = pot_outline.replace('MULTIPOLYGON (((', '').replace(')))', '')
		var arr = pot_outline.split(',');
		var xTotal = 0.0
		var yTotal = 0.0
		var leng = arr.length
		var cx = 0.0
		var cy = 0.0
        for(var i=0;i < leng ; i++){
            var jwdArr = arr[i].split(' ')
			xTotal = xTotal+Number(jwdArr[0])
            yTotal = yTotal+Number(jwdArr[1])
		}
		if(leng > 0){
			cx = xTotal/leng
			cy = yTotal/leng
		}
		var location = cy+','+cx


        flowFile = session.putAttribute(flowFile, 'location', location)

        session.transfer(flowFile, REL_SUCCESS)

	} catch(e) {
        log.error('Something went wrong', e)
        session.transfer(flowFile, REL_FAILURE)
    }
}
